// DonorMap.jsx — minimal, robust marker test
import React, { useEffect, useRef, useState } from "react";
import {
  GoogleMap,
  Marker,
  Circle,
  useJsApiLoader
} from "@react-google-maps/api";

const containerStyle = { width: "100%", height: "400px" };

export default function DonorMap({ recipientLocation, donors = [], searchRadiusMiles = 25 }) {
  const mapRef = useRef(null);
  const [mapReady, setMapReady] = useState(false);

  const hasUserLocation =
    recipientLocation &&
    !Number.isNaN(Number(recipientLocation.latitude)) &&
    !Number.isNaN(Number(recipientLocation.longitude));

  const center = hasUserLocation
    ? { lat: Number(recipientLocation.latitude), lng: Number(recipientLocation.longitude) }
    : { lat: 12.9716, lng: 77.5946 }; // fallback center

  const radiusMeters = Number(searchRadiusMiles) * 1609.34;
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  const { isLoaded, loadError } = useJsApiLoader({ googleMapsApiKey: apiKey });

  useEffect(() => {
    console.log("DonorMap: isLoaded=", isLoaded, "hasUserLocation=", hasUserLocation, "recipientLocation=", recipientLocation);
  }, [isLoaded, hasUserLocation, recipientLocation]);

  if (loadError) return <div>Map load error: {String(loadError)}</div>;
  if (!isLoaded) return null;

  // Render map even if no user location so dev can see console logs
  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={hasUserLocation ? 13 : 11}
      onLoad={(map) => {
        mapRef.current = map;
        setMapReady(true);
      }}
    >
      {/* Show default marker for user (no custom icon) */}
      {hasUserLocation && (
        <Marker
          position={center}
          title="Your location"
          // no icon here — use default marker to avoid icon-related failures
          onLoad={(marker) => console.log("User marker loaded:", marker)}
        />
      )}

      {/* Circle showing radius */}
      {hasUserLocation && (
        <Circle
          center={center}
          radius={radiusMeters}
          options={{ strokeColor: "#1976D2", fillOpacity: 0.06, clickable: false }}
          onLoad={(c) => console.log("Circle loaded:", c)}
        />
      )}

      {/* Donor markers (default markers) */}
      {Array.isArray(donors) &&
        donors.map((donor) => {
          const lat = Number(donor?.location?.latitude);
          const lng = Number(donor?.location?.longitude);
          if (Number.isNaN(lat) || Number.isNaN(lng)) return null;
          return (
            <Marker
              key={donor.id || donor.uid || donor.email}
              position={{ lat, lng }}
              onClick={() => console.log("donor clicked", donor)}
            />
          );
        })}
    </GoogleMap>
  );
}
