// DonorMap.jsx — FINAL FIXED VERSION
import React, { useEffect, useRef, useState } from "react";
import { GoogleMap, useJsApiLoader } from "@react-google-maps/api";

const containerStyle = {
  width: "100%",
  height: "400px"
};

// Helper to generate an SVG marker icon (no external images required)
function makeSvgIcon(color, label = "", size = 36) {
  const svg = encodeURIComponent(
    `<svg xmlns="http://www.w3.org/2000/svg" width="${size}" height="${size}">
       <circle cx="${size/2}" cy="${Math.floor(size*0.36)}" r="${Math.floor(size*0.22)}" fill="${color}" />
       <rect x="${Math.floor(size*0.22)}" y="${Math.floor(size*0.68)}"
             width="${Math.floor(size*0.56)}" height="${Math.floor(size*0.16)}"
             fill="${color}" rx="${Math.floor(size*0.04)}" />
       <text x="${size/2}" y="${Math.floor(size*0.46)}"
             font-size="${Math.floor(size*0.24)}"
             font-family="Arial" text-anchor="middle"
             fill="#FFF">${label}</text>
     </svg>`
  );

  const icon = {
    url: `data:image/svg+xml;charset=UTF-8,${svg}`
  };

  if (window.google && window.google.maps?.Size) {
    icon.scaledSize = new window.google.maps.Size(size, size);
  }

  return icon;
}

export default function DonorMap({
  recipientLocation,
  donors = [],
  searchRadiusMiles = 25
}) {
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
  const { isLoaded, loadError } = useJsApiLoader({ googleMapsApiKey: apiKey });

  const mapRef = useRef(null);
  const markersRef = useRef([]);

  // *** KEY FIX ***
  const [mapReady, setMapReady] = useState(false);

  // Normalize recipientLocation → {lat, lng}
  const normalized = (() => {
    if (!recipientLocation) return null;
    if (typeof recipientLocation.latitude === "number")
      return {
        lat: Number(recipientLocation.latitude),
        lng: Number(recipientLocation.longitude)
      };
    if (recipientLocation.coords)
      return {
        lat: Number(recipientLocation.coords.latitude),
        lng: Number(recipientLocation.coords.longitude)
      };
    return null;
  })();

  useEffect(() => {
    console.log(
      "DonorMap render — isLoaded:",
      isLoaded,
      " mapReady:",
      mapReady,
      " normalized:",
      normalized,
      " donors:",
      donors.length
    );
  });

  // FINAL MARKER PLACEMENT EFFECT
  useEffect(() => {
    if (!isLoaded) return;
    if (!mapReady) return; // <-- ensures mapRef.current is valid
    if (!mapRef.current) return;

    console.log("DonorMap: Placing markers…");

    const map = mapRef.current;

    // Clear old markers
    markersRef.current.forEach((m) => {
      if (m && m.setMap) m.setMap(null);
    });
    markersRef.current = [];

    // ---------------------------------------------------------------------
    // 1) Fallback Marker (always visible)
    // ---------------------------------------------------------------------
    const fallbackPos = { lat: 12.9716, lng: 77.5946 };
    const fallback = new window.google.maps.AdvancedMarkerElement({
      position: fallbackPos,
      map,
      title: "FALLBACK"
    });
    markersRef.current.push(fallback);
    console.log("Fallback marker added →", fallbackPos);

    // ---------------------------------------------------------------------
    // 2) User marker
    // ---------------------------------------------------------------------
    if (normalized) {
      const userMarker = new window.google.maps.AdvancedMarkerElement({
        position: normalized,
        map,
        title: "Your Location",
        icon: makeSvgIcon("#ff5252", "U")
      });
      markersRef.current.push(userMarker);
      console.log("User marker added →", normalized);

      map.setCenter(normalized);
      map.setZoom(13);
    } else {
      console.log("No user location detected.");
    }

    // ---------------------------------------------------------------------
    // 3) Donor markers
    // ---------------------------------------------------------------------
    (Array.isArray(donors) ? donors : []).forEach((d, i) => {
      const lat = Number(d?.location?.latitude);
      const lng = Number(d?.location?.longitude);

      if (Number.isNaN(lat) || Number.isNaN(lng)) {
        console.log("Skipping donor, invalid coords:", d);
        return;
      }

      const pos = { lat, lng };

      const donorMarker = new window.google.maps.AdvancedMarkerElement({
        position: pos,
        map,
        title: d.name || d.email || `Donor ${i + 1}`,
        icon: makeSvgIcon("#4caf50", String(i + 1))
      });

      markersRef.current.push(donorMarker);
      console.log("Donor marker added:", pos);
    });

    // Debug the final state
    setTimeout(() => {
      try {
        console.log("Map center:", map.getCenter().toJSON());
        console.log("Map zoom:", map.getZoom());
      } catch (e) {
        console.log("Error reading map state:", e);
      }
    }, 200);
  }, [isLoaded, mapReady, normalized, donors, searchRadiusMiles]);

  // ---------------------------------------------------------------------
  // Rendering
  // ---------------------------------------------------------------------
  if (loadError) {
    return <div>Map failed to load: {String(loadError)}</div>;
  }
  if (!isLoaded) return null;

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={normalized || { lat: 12.9716, lng: 77.5946 }}
      zoom={normalized ? 13 : 11}
      onLoad={(map) => {
        mapRef.current = map;
        console.log("GoogleMap onLoad → mapRef set");
        setMapReady(true); // KEY FIX — triggers marker effect
      }}
    />
  );
}
