import "./Search.css";
import React, { useState, useEffect } from "react";

export default function Search({
  setResults,
  setRecipientLocation,
  setUserHasSearched,
}) {
  const [bloodType, setBloodType] = useState("");
  const [rangeKm, setRangeKm] = useState(5);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const existing = localStorage.getItem("registeredUsers");
        if (!existing) {
          const r = await fetch("/data/users.json");
          const data = await r.json();
          localStorage.setItem(
            "registeredUsers",
            JSON.stringify(Array.isArray(data) ? data : [])
          );
        }
      } catch (_) {}
    };
    loadUsers();
  }, []);

  const haversine = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = ((lat2 - lat1) * Math.PI) / 180;
    const dLon = ((lon2 - lon1) * Math.PI) / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) *
        Math.cos(lat2 * Math.PI / 180) *
        Math.sin(dLon / 2) ** 2;

    return R * 2 * Math.asin(Math.sqrt(a));
  };

  const handleSearch = () => {
    setError(null);

    if (!bloodType) {
      setError("Select blood type");
      return;
    }

    setLoading(true);

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const latNum = Number(pos.coords.latitude);
        const lngNum = Number(pos.coords.longitude);

        if (!Number.isFinite(latNum) || !Number.isFinite(lngNum)) {
          setError("Invalid GPS");
          setLoading(false);
          return;
        }

        setRecipientLocation({ lat: latNum, lng: lngNum });

        const list = JSON.parse(
          localStorage.getItem("registeredUsers") || "[]"
        );

        const out = list.filter((u) => {
          const dLat = Number(u.location?.latitude);
          const dLng = Number(u.location?.longitude);
          if (!Number.isFinite(dLat) || !Number.isFinite(dLng)) return false;

          const type = (u.bloodType || u.bloodGroup || "").toUpperCase();
          if (type !== bloodType.toUpperCase()) return false;

          const dist = haversine(latNum, lngNum, dLat, dLng);
          return dist <= rangeKm;
        });

        setResults(out);
        setUserHasSearched(true);
        setLoading(false);
      },
      () => {
        setError("Location permission denied");
        setLoading(false);
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
    );
  };

  return (
    <section className="search-section" id="search">
      <div className="container">
        <h2 className="title">Find Blood Donors</h2>

        <form
          className="search-form"
          onSubmit={(e) => {
            e.preventDefault();
            handleSearch();
          }}
        >
          <div className="form-group">
            <label>Blood Type Needed</label>
            <select
              value={bloodType}
              onChange={(e) => setBloodType(e.target.value)}
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          <div className="form-group">
            <label>Search Radius (km)</label>
            <select
              value={rangeKm}
              onChange={(e) => setRangeKm(Number(e.target.value))}
            >
              <option value={5}>5 km</option>
              <option value={10}>10 km</option>
              <option value={20}>20 km</option>
              <option value={30}>30 km</option>
            </select>
          </div>

          <div className="form-group-full">
            <button className="search-section-button" type="submit">
              Search Donors
            </button>
          </div>

          {loading && <p>Searching...</p>}
          {error && <p className="error-text">{error}</p>}
        </form>
      </div>
    </section>
  );
}
