// Search.jsx
import './Search.css';
import React, { useState, useEffect } from "react";

export default function Search({ setResults, setRecipientLocation, setUserHasSearched }) {
  const [bloodType, setBloodType] = useState("");
  const [rangeKm, setRangeKm] = useState(5);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // On mount: ensure localStorage.registeredUsers exists by loading /data/users.json if needed
  useEffect(() => {
    const seed = async () => {
      try {
        const existing = localStorage.getItem("registeredUsers");
        if (!existing) {
          const res = await fetch("/data/users.json");
          if (!res.ok) throw new Error("Failed to fetch users.json: " + res.status);
          const data = await res.json();
          localStorage.setItem("registeredUsers", JSON.stringify(Array.isArray(data) ? data : []));
          console.log("Seeded registeredUsers from /data/users.json, count:", (data || []).length);
        }
      } catch (e) {
        console.warn("Could not seed users.json to localStorage:", e);
      }
    };
    seed();
  }, []);

  const haversine = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) *
        Math.cos(lat2 * Math.PI / 180) *
        Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.asin(Math.sqrt(a));
  };

  const handleSearch = () => {
    setError(null);
    if (!bloodType) {
      setError("Please select a blood type");
      return;
    }
    setLoading(true);

    if (!navigator.geolocation) {
      setError("Geolocation not supported");
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const lat = Number(pos.coords.latitude);
        const lng = Number(pos.coords.longitude);
        if (Number.isNaN(lat) || Number.isNaN(lng)) {
          setError("Invalid coordinates");
          setLoading(false);
          return;
        }

        setRecipientLocation({ latitude: lat, longitude: lng });

        const stored = JSON.parse(localStorage.getItem("registeredUsers") || "[]");
        const filtered = (stored || []).filter((u) => {
          const dLat = Number(u.location?.latitude ?? u.location?.lat);
          const dLng = Number(u.location?.longitude ?? u.location?.lng);
          if (Number.isNaN(dLat) || Number.isNaN(dLng)) return false;
          const type = (u.bloodType || u.bloodGroup || "").toString().toUpperCase();
          if (type !== bloodType.toUpperCase()) return false;
          const dist = haversine(lat, lng, dLat, dLng);
          return dist <= Number(rangeKm);
        });

        setResults(filtered);
        setUserHasSearched(true);
        setLoading(false);
      },
      (err) => {
        console.error("Geolocation error:", err);
        setError("Location permission denied or unavailable");
        setLoading(false);
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
    );
  };

  return (
    <section id="search" className="search-section">
      <div className="container">
        <h2 className="title">Find Blood Donors</h2>
        <form
          className="search-form"
          onSubmit={(e) => { e.preventDefault(); handleSearch(); }}
        >
          <div className="form-group">
            <label htmlFor="bloodtype">Blood Type Needed</label>
            <select id="bloodtype" value={bloodType} onChange={(e) => setBloodType(e.target.value)}>
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option><option value="A-">A-</option>
              <option value="B+">B+</option><option value="B-">B-</option>
              <option value="AB+">AB+</option><option value="AB-">AB-</option>
              <option value="O+">O+</option><option value="O-">O-</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="radius">Search Radius (km)</label>
            <select id="radius" value={rangeKm} onChange={(e) => setRangeKm(e.target.value)}>
              <option value={5}>5 km</option>
              <option value={10}>10 km</option>
              <option value={20}>20 km</option>
              <option value={30}>30 km</option>
            </select>
          </div>

          <div className="form-group form-group-full">
            <button type="submit" className="search-section-button">Search Donors</button>
          </div>

          {loading && <p>Searching for donors...</p>}
          {error && <p className="error-text">Error: {error}</p>}
        </form>
      </div>
    </section>
  );
}
