import { useState } from "react";
import './ContactModal.css';

function ContactModal({ user, onClose }) {
  if (!user) return null;

  return (
    <div id="contactModal" className="modal">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Contact Donor</h3>
          <button className="modal-close" onClick={onClose}>
            &times;
          </button>
        </div>

        <div className="modal-body">
          <p><strong>Name:</strong> {user.fullname}</p>
          <p><strong>Blood Group:</strong> {user.bloodType}</p>
          <p><strong>Location:</strong> {user.address}</p>
          <p>
            <strong>Phone:</strong>{" "}
            <a href={`tel:${user.phoneNo}`}>{user.phoneNo}</a>
          </p>
          <p>
            <strong>Email:</strong>{" "}
            <a href={`mailto:${user.email}`}>{user.email}</a>
          </p>
          <p>
  Status:{" "}
  <span
    className={
      user.status === "Available" ? "status-available" : "status-unavailable"
    }
  >
    {user.status === "Available" ? "Available" : "Not Available"}
  </span>
</p>



          <p className="contact-note">
            Please be respectful when contacting donors. Explain your situation
            clearly and provide your contact information.
          </p>
        </div>

        <div className="modal-footer">
          <button className="contact-button" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
}

export default ContactModal;
