import React, { useState } from "react";

export default function Search({ setResults, setRecipientLocation, setUserHasSearched, setSelectedDonor }) {
  const [bloodType, setBloodType] = useState("");
  const [rangeKm, setRangeKm] = useState(5);

  const haversine = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * (Math.PI / 180);
    const dLon = (lon2 - lon1) * (Math.PI / 180);
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * (Math.PI / 180)) *
        Math.cos(lat2 * (Math.PI / 180)) *
        Math.sin(dLon / 2) ** 2;
    return R * 2 * Math.asin(Math.sqrt(a));
  };

  const handleSearch = () => {
    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const userLat = pos.coords.latitude;
        const userLng = pos.coords.longitude;

        setRecipientLocation({ lat: userLat, lng: userLng });

        const storedUsers = JSON.parse(localStorage.getItem("registeredUsers")) || [];

        const filtered = storedUsers.filter((u) => {
          if (u.bloodType !== bloodType) return false;
          const dist = haversine(userLat, userLng, u.location.latitude, u.location.longitude);
          return dist <= rangeKm;
        });

        setResults(filtered);
        setUserHasSearched(true);
      },
      (err) => {
        console.log("Geolocation error:", err);
      }
    );
  };

  return (
    <div className="search-box">
      <select value={bloodType} onChange={(e) => setBloodType(e.target.value)}>
        <option value="">Select Blood Type</option>
        <option value="A+">A+</option><option value="A-">A-</option>
        <option value="B+">B+</option><option value="B-">B-</option>
        <option value="O+">O+</option><option value="O-">O-</option>
        <option value="AB+">AB+</option><option value="AB-">AB-</option>
      </select>

      <select value={rangeKm} onChange={(e) => setRangeKm(Number(e.target.value))}>
        <option value={5}>5 km</option>
        <option value={10}>10 km</option>
        <option value={20}>20 km</option>
        <option value={30}>30 km</option>
      </select>

      <button onClick={handleSearch}>Search</button>
    </div>
  );
}
