// DonorMap.jsx — use this exact file
import React, { useEffect, useRef } from "react";
import { GoogleMap, useJsApiLoader } from "@react-google-maps/api";

const containerStyle = { width: "100%", height: "400px" };

export default function DonorMap({ recipientLocation, donors = [] }) {
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
  const { isLoaded, loadError } = useJsApiLoader({ googleMapsApiKey: apiKey });

  const normalized = (() => {
    if (!recipientLocation) return null;
    if (typeof recipientLocation.latitude === "number") return { lat: Number(recipientLocation.latitude), lng: Number(recipientLocation.longitude) };
    if (typeof recipientLocation.lat === "number") return { lat: Number(recipientLocation.lat), lng: Number(recipientLocation.lng) };
    if (recipientLocation.coords && typeof recipientLocation.coords.latitude === "number") return { lat: Number(recipientLocation.coords.latitude), lng: Number(recipientLocation.coords.longitude) };
    return null;
  })();

  useEffect(() => {
    if (!isLoaded || !mapRef.current) return;
    const map = mapRef.current;

    // clear old markers
    markersRef.current.forEach(m => m.setMap(null));
    markersRef.current = [];

    // helper: SVG data URL icon → no external image dependency
    const makeSvgIcon = (color, label, size=36) => {
      const svg = encodeURIComponent(
        `<svg xmlns="http://www.w3.org/2000/svg" width="${size}" height="${size}">
          <circle cx="${size/2}" cy="${size*0.38}" r="${size*0.22}" fill="${color}" />
          <rect x="${size*0.25}" y="${size*0.7}" width="${size*0.5}" height="${size*0.18}" fill="${color}" rx="${size*0.04}" />
          <text x="${size/2}" y="${size*0.46}" font-size="${Math.floor(size*0.26)}" font-family="Arial" text-anchor="middle" fill="#FFF">${label||''}</text>
        </svg>`
      );
      return { url: `data:image/svg+xml;charset=UTF-8,${svg}`, scaledSize: new window.google.maps.Size(size, size) };
    };


    // fallback marker (always)
    /* const fallback = new window.google.maps.Marker({
      position: { lat: 12.9716, lng: 77.5946 },
      map,
      title: "FALLBACK",
      icon: makeSvgIcon('#1976D2','F')
    });
    markersRef.current.push(fallback); */

    if (normalized) {
      const userMarker = new window.google.maps.Marker({
        position: normalized,
        map,
        title: "YOU",
        icon: makeSvgIcon('#ff5252','U'),
        zIndex: 9999
      });
      markersRef.current.push(userMarker);
      map.setCenter(normalized);
      map.setZoom(13);
    }

    const infoWindow = new window.google.maps.InfoWindow();

    donors.forEach((d,i)=>{
      const lat = Number(d?.location?.latitude), lng = Number(d?.location?.longitude);
      if (Number.isNaN(lat)||Number.isNaN(lng)) return;
      const mk = new window.google.maps.Marker({
        position:{ lat, lng },
        map,
        title: d.name || `donor-${i}`,
        icon: makeSvgIcon('#4caf50', String(i+1))
      });

      mk.addListener('click', () => {
        const content = `<div style="min-width:160px">
                          <strong>${d.name || 'Donor'}</strong>
                          <div>Blood: ${d.bloodGroup || d.bloodType || 'N/A'}</div>
                          <div>Contact: ${d.contact || d.phoneNo || 'N/A'}</div>
                        </div>`;
        infoWindow.setContent(content);
        infoWindow.open({ anchor: mk, map });
      });

      markersRef.current.push(mk);
    });


    setTimeout(() => {
      console.log('Force markers placed. Center:', map.getCenter() && map.getCenter().toJSON(), 'zoom:', map.getZoom());
    }, 200);

    return () => {
      markersRef.current.forEach(m => m && m.setMap(null));
      markersRef.current = [];
    };
  }, [isLoaded, normalized, donors]);

  if (loadError) return <div>Map load error: {String(loadError)}</div>;
  if (!isLoaded) return null;

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={normalized || { lat: 12.9716, lng: 77.5946 }}
      zoom={normalized ? 13 : 11}
      onLoad={(m) => (mapRef.current = m)}
    />
  );
}
