import './Search.css';
import React, { useState, useEffect } from "react";


function Search({ setResults, setRecipientLocation }) {
  const [bloodType, setBloodType] = useState("");
  //const [location, setLocation] = useState("");
  const [radius, setRadius] = useState("25");
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  //const [recipientLocation, setRecipientLocation] = useState(null);



  useEffect(() => {
    fetch('/data/users.json')
      .then(res => res.json())
      .then(data => setUsers(data))
      .catch(err => console.error(err));
  }, []);


  //Haversine formula for distance in km between two lat/lng points
  function getDistanceFromLocationInKm(lat1, long1, lat2, long2) {
    const R = 6371; //radius of earth in km
    const dLat = (lat2-lat1) * Math.PI/180;
    const dLon = (long2-long1) * Math.PI/180;
    const a = Math.sin(dLat/2)*Math.sin(dLat/2)+Math.cos(lat1*Math.PI/180)* Math.cos(lat2*Math.PI/180)*Math.sin(dLon/2)*Math.sin(dLon/2);
    const  c = 2*Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    const d = R*c;
    return d; 
  }

  const searchDonors = () => {
    setError(null);

    if (!bloodType) {
      alert("Please select a blood type");
      return;
    }
    
    setLoading(true);

    if(!navigator.geolocation){
      setError('Geolocation not supported by your browser');
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setRecipientLocation({ latitude, longitude });
        // Convert radius from miles to km (1 mile = 1.60934 km)
        const  searchRadiusKm = Number(radius)*1.60934;

        //Filter donors by blood group and radius based on geo distance
        const filtered = users.filter(user => {
          // Make sure donor has geo info
          if(
            user.bloodGroup && user.location && typeof user.location.latitude ==="number" && typeof user.location.longitude === "number"
          ) {
            const distance = getDistanceFromLocationInKm(
              latitude,
              longitude,
              user.location.latitude,
              user.location.longitude
            );
            return (
              user.bloodGroup.toLowerCase() === bloodType.toLowerCase() && distance <= searchRadiusKm
            );
          }
          return false;
        });

        setResults(filtered);
        setLoading(false);
      },
      (geoError) => {
        setError("Location permission denied or unavailable: "+geoError.message);
        setLoading(false);
      }
    )
    
  };

  return (
    <section id="search" className="search-section">
      <div className="container">
        <h2 className="title">Find Blood Donors</h2>
        <form 
          className="search-form"
          onSubmit={e => {
            e.preventDefault();
            searchDonors();
          }}
        >
          {/* <div className="results">
            {results.length > 0 ? (
              results.map(user => (
                <div key={user.id} className="donor-card">
                  <p><strong>{user.name}</strong></p>
                  <p>Blood Group: {user.bloodGroup}</p>
                  <p>City: {user.city}</p>
                  <p>Contact: {user.contact}</p>
                </div>
              ))
            ) : (
              <p>No donors found</p>
            )}
          </div> */}

          <div className="form-group">
            <label htmlFor="bloodtype" className="form-label">Blood Type Needed</label>
            <select
              id="bloodtype"
              className="form-control"
              value={bloodType}
              onChange={(e) => setBloodType(e.target.value)}
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          {/* <div className="form-group">
            <label htmlFor="location" className="form-label">Location</label>
            <input
              type="text"
              id="location"
              className="form-control"
              placeholder="Enter city, state"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
          </div> */}

          <div className="form-group">
            <label htmlFor="radius" className="form-label">Search Radius</label>
            <select
              id="radius"
              className="form-control"
              value={radius}
              onChange={(e) => setRadius(e.target.value)}
            >
              <option value="10">10 miles</option>
              <option value="25">25 miles</option>
              <option value="50">50 miles</option>
              <option value="100">100 miles</option>
            </select>
          </div>

          {/* The button spans all columns below */}
          <div className="form-group form-group-full">
            <button type="button" className="search-section-button" onClick={searchDonors}>
              <span role="img" aria-label="search">üîç</span> Search Donors
            </button>
          </div>
        </form>
      </div>
    </section>
  );
}

export default Search;
