import React, { useState } from "react";
import "./SearchResult.css";
import ContactModal from "./ContactModal";

export default function SearchResult({ results = [], focusOn }) {
  const [selectedUser, setSelectedUser] = useState(null);

  return (
    <section className="search-result">
      <div className="container">
        <h3 className="results-title">Available Donors</h3>

        {results.length === 0 ? (
          <p>No donors found within this range.</p>
        ) : (
          <div className="donor-grid">
            {results.map((u, idx) => {
              const lat = u.location.latitude;
              const lng = u.location.longitude;

              return (
                <div key={idx} className="donor-card">
                  <h4>{u.fullname}</h4>
                  <p>{u.email}</p>

                  <button onClick={() => setSelectedUser(u)} className="contact-btn">
                    Contact
                  </button>

                  <button
                    className="focus-btn"
                    onClick={() => focusOn(lat, lng)}
                  >
                    Show on map
                  </button>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {selectedUser && (
        <ContactModal user={selectedUser} onClose={() => setSelectedUser(null)} />
      )}
    </section>
  );
}
