// Search.jsx
import './Search.css';
import React, { useState, useEffect } from "react";

function Search({ setResults, setRecipientLocation }) {
  const [bloodType, setBloodType] = useState("");
  const [radius, setRadius] = useState("25");
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // make sure file path is correct in production/public folder
    fetch('/data/users.json')
      .then(res => {
        if (!res.ok) throw new Error('Failed to load users.json: ' + res.status);
        return res.json();
      })
      .then(data => setUsers(data || []))
      .catch(err => {
        console.error('Users load error:', err);
        setUsers([]);
      });
  }, []);

  // Haversine formula (distance in km)
  function getDistanceFromLocationInKm(lat1, long1, lat2, long2) {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (long2 - long1) * Math.PI / 180;
    const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  }

  const searchDonors = () => {
    console.log('Search button clicked');
    setError(null);

    if (!bloodType) {
      // use alert in dev, but not silent failure
      alert("Please select a blood type");
      return;
    }

    setLoading(true);

    if (!navigator.geolocation) {
      setError('Geolocation not supported by your browser');
      setLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        console.log('Position received:', position);
        const { latitude, longitude } = position.coords;
        setRecipientLocation({ latitude: Number(latitude), longitude: Number(longitude) });

        // convert miles -> km
        const searchRadiusKm = Number(radius) * 1.60934;

        // defensive: make sure users is an array
        const userList = Array.isArray(users) ? users : [];

        const filtered = userList.filter(user => {
          // support both naming patterns and ensure numeric lat/lng
          const uLat = user?.location?.latitude;
          const uLng = user?.location?.longitude;
          const userBlood = user.bloodGroup || user.bloodType || "";

          if (
            userBlood &&
            typeof uLat === "number" &&
            typeof uLng === "number"
          ) {
            const distance = getDistanceFromLocationInKm(
              latitude,
              longitude,
              uLat,
              uLng
            );
            return userBlood.toLowerCase() === bloodType.toLowerCase() && distance <= searchRadiusKm;
          }
          return false;
        });

        console.log('Filtered donors:', filtered);
        setResults(filtered);
        setLoading(false);
      },
      (geoError) => {
        // FIX: use the correct param (geoError) instead of undefined 'error'
        console.error('Geolocation error:', geoError);
        setError("Location permission denied or unavailable: " + (geoError?.message || 'Unknown error'));
        setLoading(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000
      }
    );
  };

  return (
    <section id="search" className="search-section">
      <div className="container">
        <h2 className="title">Find Blood Donors</h2>
        <form
          className="search-form"
          onSubmit={e => {
            e.preventDefault();
            searchDonors();
          }}
        >
          <div className="form-group">
            <label htmlFor="bloodtype" className="form-label">Blood Type Needed</label>
            <select
              id="bloodtype"
              className="form-control"
              value={bloodType}
              onChange={(e) => setBloodType(e.target.value)}
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="radius" className="form-label">Search Radius</label>
            <select
              id="radius"
              className="form-control"
              value={radius}
              onChange={(e) => setRadius(e.target.value)}
            >
              <option value="10">10 miles</option>
              <option value="25">25 miles</option>
              <option value="50">50 miles</option>
              <option value="100">100 miles</option>
            </select>
          </div>

          <div className="form-group form-group-full">
            <button type="submit" className="search-section-button" >
              Search Donors
            </button>
          </div>

          {loading && <p>Searching for donors...</p>}
          {error && <p className="error-text">Error: {error}</p>}
        </form>
      </div>
    </section>
  );
}

export default Search;
