// DonorMap.jsx
import React from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

function DonorMap({ recipientLocation, donors = [] }) {
  const [selectedDonor, setSelectedDonor] = React.useState(null);

  if (!recipientLocation) return null;

  const center = {
    lat: recipientLocation.latitude,
    lng: recipientLocation.longitude
  };

  // NOTE: Replace the API key string with an env var in production: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY || "AIzaSyDcG9acW8tmFGsxYTGrrvoKjdafi4sP_-k";

  return (
    <LoadScript googleMapsApiKey={apiKey}>
      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={12}>
        <Marker position={center} icon={{ url: "http://maps.google.com/mapfiles/ms/icons/blue-dot.png" }} />

        {Array.isArray(donors) && donors.map((donor) => (
          donor?.location?.latitude && donor?.location?.longitude && (
            <Marker
              key={donor.id || donor.uid || donor.email}
              position={{ lat: donor.location.latitude, lng: donor.location.longitude }}
              onClick={() => setSelectedDonor(donor)}
            />
          )
        ))}

        {selectedDonor && (
          <InfoWindow
            position={{ lat: selectedDonor.location.latitude, lng: selectedDonor.location.longitude }}
            onCloseClick={() => setSelectedDonor(null)}
          >
            <div>
              <h3>{selectedDonor.name || selectedDonor.fullname || "Donor"}</h3>
              <p>Blood Group: {selectedDonor.bloodGroup || selectedDonor.bloodType}</p>
              <p>Contact: {selectedDonor.contact || selectedDonor.phoneNo}</p>
              <p>City: {selectedDonor.city}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
}

export default DonorMap;
