import React, { useState, useCallback, useRef } from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import Header from "./components/Header";
import Hero from "./components/Hero";
import Search from "./components/Search";
import SearchResult from "./components/SearchResult";
import Register from "./components/Register";
import About from "./components/About";
import Footer from "./components/Footer";
import ContactModal from "./components/ContactModal";
import DonorMap from "./components/DonorMap";

import "./styles/global.css";

export default function App() {
  // restored login modal state (Header expects these)
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);

  const [searchResults, setSearchResults] = useState([]);
  const [recipientLocation, setRecipientLocation] = useState(null);
  const [mapController, setMapControllerState] = useState(null);
  const [userHasSearched, setUserHasSearched] = useState(false);

  const mapContainerRef = useRef(null);
  const pendingFocusRef = useRef([]);

  const setMapController = useCallback((controller) => {
    setMapControllerState(controller);
    if (controller && pendingFocusRef.current.length > 0) {
      pendingFocusRef.current.forEach(({ lat, lng }) => {
        try { controller.focusOn(lat, lng); } catch (e) { console.warn(e); }
      });
      pendingFocusRef.current = [];
    }
  }, []);

  const focusOn = useCallback((lat, lng) => {
    if (mapController && typeof mapController.focusOn === "function") {
      mapController.focusOn(lat, lng);
    } else {
      pendingFocusRef.current.push({ lat, lng });
    }
  }, [mapController]);

  return (
    <>
      <div>
        <Header
          isLoginModalOpen={isLoginModalOpen}
          setIsLoginModalOpen={setIsLoginModalOpen}
        />

        <Hero />

        <Search
          setResults={setSearchResults}
          setRecipientLocation={setRecipientLocation}
          setUserHasSearched={setUserHasSearched}
        />

        {userHasSearched && recipientLocation && (
          <DonorMap
            recipientLocation={recipientLocation}
            donors={searchResults}
            setMapController={setMapController}
            mapContainerRef={mapContainerRef}
          />
        )}

        {userHasSearched && (
          <SearchResult results={searchResults} focusOn={focusOn} />
        )}

        <Register setIsLoginModalOpen={setIsLoginModalOpen} />
        <About />
        <Footer />
        <ContactModal />
      </div>

      <ToastContainer />
    </>
  );
}
