// SearchResult.jsx
import { useState } from "react";
import ContactModal from "./ContactModal";
import "./SearchResult.css";

function SearchResult({ results = [] }) {
  const [selectedUser, setSelectedUser] = useState(null);

  // show section even if there are no results — provide feedback
  return (
    <section className="search-result">
      <div className="container">
        <div id="searchResults" className="search-results">
          <h3 className="results-title">Available Donors</h3>

          {(!results || results.length === 0) ? (
            <p>No donors found for the selected blood type and radius.</p>
          ) : (
            <div id="donorCards" className="donor-grid">
              {results.map((user) => (
                <div key={user.id || user.uid || user.email} className="donor-card">
                  <div className="donor-card-header">
                    <span className="donor-name">{user.name || user.fullname}</span>
                    <span className="donor-blood">{user.bloodGroup || user.bloodType}</span>
                  </div>
                  <div className="donor-details">
                    <p>Location: {user.city || user.address}</p>
                    <p>Contact: {user.contact || user.phoneNo}</p>
                    <p>Email: {user.email}</p>
                    <p>
                      Status:{" "}
                      <span className={user.status === "Available" ? "status-available" : "status-unavailable"}>
                        {user.status === "Available" ? "Available" : "Not Available"}
                      </span>
                    </p>
                  </div>
                  <button className="contact-btn" onClick={() => setSelectedUser(user)}>
                    Contact Donor
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {selectedUser && (
        <ContactModal user={selectedUser} onClose={() => setSelectedUser(null)} />
      )}
    </section>
  );
}

export default SearchResult;
