import { useState } from "react";
import ContactModal from "./ContactModal";
import "./SearchResult.css";

function SearchResult({ results = [] }) {
  const [selectedUser, setSelectedUser] = useState(null);

  if (!results || results.length === 0) return null;

  return (
    <section className="search-result">
      <div className="container">
        <div id="searchResults" className="search-results">
          <h3 className="results-title">Available Donors</h3>
          <div id="donorCards" className="donor-grid">
            {results.map((user) => (
              <div key={user.id} className="donor-card">
                <div className="donor-card-header">
                  <span className="donor-name">{user.name}</span>
                  <span className="donor-blood">{user.bloodGroup}</span>
                </div>
                <div className="donor-details">
                  <p>Location: {user.city}</p>
                  <p>Contact: {user.contact}</p>
                  <p>Email: {user.email}</p>
                  <p>
  Status:{" "}
  <span
    className={
      user.status === "Available" ? "status-available" : "status-unavailable"
    }
  >
    {user.status === "Available" ? "Available" : "Not Available"}
  </span>
</p>
                </div>
                <button
                  className="contact-btn"
                  onClick={() => setSelectedUser(user)}
                >
                  Contact Donor
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {selectedUser && (
        <ContactModal user={selectedUser} onClose={() => setSelectedUser(null)} />
      )}
    </section>
  );
}

export default SearchResult;
