// DonorMap.jsx
import React, { useEffect, useRef, useState } from "react";
import { GoogleMap, useJsApiLoader } from "@react-google-maps/api";

const containerStyle = { width: "100%", height: "400px" };

export default function DonorMap({
  recipientLocation,
  donors,
  setMapController,
  mapContainerRef
}) {
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
  const { isLoaded, loadError } = useJsApiLoader({ googleMapsApiKey: apiKey });

  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const infoWindowRef = useRef(null);
  const [mapReady, setMapReady] = useState(false);

  const normalized = recipientLocation
    ? { lat: Number(recipientLocation.lat), lng: Number(recipientLocation.lng) }
    : null;

  useEffect(() => {
    if (!isLoaded || !mapReady || !mapRef.current) return;

    const map = mapRef.current;

    markersRef.current.forEach((m) => m.setMap(null));
    markersRef.current = [];

    if (!infoWindowRef.current)
      infoWindowRef.current = new window.google.maps.InfoWindow();
    else infoWindowRef.current.close();

    if (normalized) {
      const userMarker = new window.google.maps.Marker({
        position: normalized,
        map,
        icon: "https://maps.gstatic.com/mapfiles/ms2/micons/blue-dot.png",
        title: "Your Location",
      });
      markersRef.current.push(userMarker);
      map.setCenter(normalized);
      map.setZoom(13);
    }

    donors.forEach((d) => {
      const lat = Number(d.location.latitude);
      const lng = Number(d.location.longitude);
      const pos = { lat, lng };

      const marker = new window.google.maps.Marker({
        position: pos,
        map,
        title: d.fullname,
      });

      marker.addListener("click", () => {
        infoWindowRef.current.setContent(
          `<div><strong>${d.fullname}</strong><br>${d.email}</div>`
        );
        infoWindowRef.current.open(map, marker);
      });

      markersRef.current.push(marker);
    });
  }, [isLoaded, mapReady, donors, normalized]);

  useEffect(() => {
    if (!isLoaded || !mapReady || !mapRef.current) {
      setMapController(null);
      return;
    }

    setMapController({
      focusOn: (lat, lng) => {
        const map = mapRef.current;
        const pos = { lat: Number(lat), lng: Number(lng) };

        map.panTo(pos);
        map.setZoom(15);

        const marker = markersRef.current.find((m) => {
          const p = m.getPosition().toJSON();
          return p.lat === pos.lat && p.lng === pos.lng;
        });

        if (marker) {
          const donor = donors.find(
            (d) =>
              Number(d.location.latitude) === pos.lat &&
              Number(d.location.longitude) === pos.lng
          );

          if (donor) {
            infoWindowRef.current.setContent(
              `<div><strong>${donor.fullname}</strong><br>${donor.email}</div>`
            );
            infoWindowRef.current.open(map, marker);
          }
        }

        mapContainerRef.current.scrollIntoView({ behavior: "smooth" });
      },
    });

    return () => setMapController(null);
  }, [isLoaded, mapReady, donors]);

  if (!isLoaded) return null;
  if (loadError) return <div>Map failed to load</div>;

  return (
    <div ref={mapContainerRef}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={normalized || { lat: 12.9716, lng: 77.5946 }}
        zoom={normalized ? 13 : 11}
        onLoad={(map) => {
          mapRef.current = map;
          setMapReady(true);
        }}
        onUnmount={() => {
          mapRef.current = null;
          setMapReady(false);
        }}
      />
    </div>
  );
}
