// src/components/ContactModal.jsx
import React, { useState, useEffect } from "react";
import "./ContactModal.css";
import { init, send } from "@emailjs/browser";

/**
 * ContactModal
 * Props:
 *  - user: donor object
 *  - recipient: (optional) object with { name, email, message, bloodType } or your app may build these fields at send time
 *  - onClose: close callback
 *
 * Environment variables required in .env (you already set):
 *  REACT_APP_EMAILJS_PUBLIC_KEY
 *  REACT_APP_EMAILJS_SERVICE_ID
 *  REACT_APP_EMAILJS_TEMPLATE_ID
 */
export default function ContactModal({ user, recipient = {}, onClose }) {
  const [sending, setSending] = useState(false);
  const [sent, setSent] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const pub = process.env.REACT_APP_EMAILJS_PUBLIC_KEY;
    if (pub) init(pub);
  }, []);

  if (!user) return null;

  const escapeHtml = (str) => {
    if (str === null || str === undefined) return "";
    return String(str)
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
  };

  const name = user.fullname || user.name || user.displayName || user.full_name || "Donor";
  const email = user.email || user.mail || "";
  const phone = user.phoneNo || user.contact || user.phone || "";
  const bloodGroup = user.bloodType || user.bloodGroup || user.blood || "Unknown";
  const address = user.address || user.city || user.location?.address || "";
  let locationText = address;
  if (!locationText) {
    const lat = user.location?.latitude ?? user.location?.lat;
    const lng = user.location?.longitude ?? user.location?.lng;
    if (Number.isFinite(Number(lat)) && Number.isFinite(Number(lng))) {
      locationText = `${Number(lat).toFixed(6)}, ${Number(lng).toFixed(6)}`;
    } else {
      locationText = "Not provided";
    }
  }

  // Build accept/reject links — these are simple examples.
  // You can change these endpoints to your backend or cloud function routes later.
  const origin = window.location.origin;
  const recipientEmail = recipient.email || ""; // pass recipient.email when calling modal
  const recipientName = recipient.name || ""; // pass recipient.name when calling modal

  const acceptLink = `${origin}/email-response?action=accept&donor=${encodeURIComponent(email)}&recipient=${encodeURIComponent(recipientEmail)}`;
  const rejectLink = `${origin}/email-response?action=reject&donor=${encodeURIComponent(email)}&recipient=${encodeURIComponent(recipientEmail)}`;

  async function handleRequest() {
    setSending(true);
    setError(null);
    setSent(false);

    const serviceId = process.env.REACT_APP_EMAILJS_SERVICE_ID;
    const templateId = process.env.REACT_APP_EMAILJS_TEMPLATE_ID;

    if (!serviceId || !templateId) {
      setError("EmailJS service/template not configured in .env");
      setSending(false);
      return;
    }

    // Template params expected by your EmailJS template.
    // Only the five variables you asked for are required in HTML: name, message, bloodtype, accept_link, reject_link
    const templateParams = {
      // Visible template vars:
      name: recipientName || "{{recipient_name}}",  // recipient's name (person requesting blood)
      message: recipient.message || recipient.msg || `Requesting ${bloodGroup} blood — please respond if available.`,
      bloodtype: recipient.bloodType || recipient.blood || bloodGroup,
      accept_link: acceptLink,
      reject_link: rejectLink,

      // Hidden / non-displayed vars (available for sending headers or other usage)
      donor_email: email,
      recipient_email: recipientEmail,
    };

    try {
      await send(serviceId, templateId, templateParams);
      console.log("EmailJS: request sent", templateParams);
      setSent(true);
    } catch (err) {
      console.error("EmailJS send error:", err);
      setError(err?.text || err?.message || "Failed to send request email");
    } finally {
      setSending(false);
    }
  }

  return (
    <div id="contactModal" className="modal" role="dialog" aria-modal="true">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Contact Donor</h3>
          <button
            className="modal-close"
            onClick={onClose}
            aria-label="Close contact modal"
          >
            &times;
          </button>
        </div>

        <div className="modal-body">
          <p><strong>Name:</strong> {escapeHtml(name)}</p>
          <p><strong>Blood Group:</strong> {escapeHtml(bloodGroup)}</p>
          <p><strong>Location:</strong> {escapeHtml(locationText)}</p>

          <p>
            <strong>Phone:</strong>{" "}
            {phone ? <a href={`tel:${escapeHtml(phone)}`}>{escapeHtml(phone)}</a> : "Not provided"}
          </p>

          <p>
            <strong>Email:</strong>{" "}
            {email ? <a href={`mailto:${escapeHtml(email)}`}>{escapeHtml(email)}</a> : "Not provided"}
          </p>

          <p className="contact-note">
            Please be respectful when contacting donors. Explain your situation clearly and provide your contact information.
          </p>

          {error && <div className="error-message" role="alert">{error}</div>}
          {sent && <div className="success-message" role="status">Request email sent to donor.</div>}
        </div>

        <div className="modal-footer">
          <button
            className="request-button"
            disabled={sending || sent}
            onClick={() => {
              // Ensure recipient data is present; if your app does not pass recipient via props,
              // adapt this to grab the recipient info from the form or app state.
              handleRequest();
            }}
          >
            {sending ? "Sending..." : sent ? "Sent" : "Request Blood"}
          </button>

          <button className="contact-button" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
}
