// DonorMap.diagnostic.jsx — paste this as DonorMap.jsx temporarily
import React, { useEffect, useRef, useState } from "react";
import { GoogleMap, Marker, Circle, useJsApiLoader } from "@react-google-maps/api";

const containerStyle = { width: "100%", height: "400px" };

export default function DonorMap({ recipientLocation, donors = [], searchRadiusMiles = 25 }) {
  const mapRef = useRef(null);
  const [mapReady, setMapReady] = useState(false);

  console.log("DonorMap render — raw recipientLocation:", recipientLocation);
  const hasUserLocation =
    recipientLocation &&
    // accept either { latitude, longitude } or { lat, lng }
    (typeof recipientLocation.latitude === "number" && typeof recipientLocation.longitude === "number") ||
    (typeof recipientLocation.lat === "number" && typeof recipientLocation.lng === "number");

  // normalize to { lat, lng } numbers
  const normalized = (() => {
    if (!recipientLocation) return null;
    if (typeof recipientLocation.latitude === "number" && typeof recipientLocation.longitude === "number") {
      return { lat: Number(recipientLocation.latitude), lng: Number(recipientLocation.longitude) };
    }
    if (typeof recipientLocation.lat === "number" && typeof recipientLocation.lng === "number") {
      return { lat: Number(recipientLocation.lat), lng: Number(recipientLocation.lng) };
    }
    // If it's the full GeolocationPosition object, extract coords if present
    if (recipientLocation.coords && typeof recipientLocation.coords.latitude === "number") {
      return { lat: Number(recipientLocation.coords.latitude), lng: Number(recipientLocation.coords.longitude) };
    }
    return null;
  })();

  console.log("DonorMap — hasUserLocation:", hasUserLocation, "normalized:", normalized);

  const fallbackCenter = { lat: 12.9716, lng: 77.5946 }; // Bengaluru
  const center = normalized || fallbackCenter;
  const radiusMeters = Number(searchRadiusMiles) * 1609.34;
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  const { isLoaded, loadError } = useJsApiLoader({ googleMapsApiKey: apiKey });

  useEffect(() => {
    console.log("DonorMap status — isLoaded:", isLoaded, "mapReady:", mapReady);
  }, [isLoaded, mapReady]);

  if (loadError) return <div>Map load error: {String(loadError)}</div>;
  if (!isLoaded) return null;

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={normalized ? 13 : 11}
      onLoad={(map) => {
        mapRef.current = map;
        setMapReady(true);
        console.log("Map onLoad, mapRef set");
        // force center & zoom immediately so marker will be visible
        if (normalized) {
          map.setCenter(normalized);
          map.setZoom(13);
          console.log("Map forced center to:", normalized);
        }
      }}
    >
      {/* Forced default user marker (no custom icon) */}
      {normalized && (
        <Marker
          position={normalized}
          title="Normalized user location"
          onLoad={(m) => console.log("User Marker loaded (object):", m, "position:", m?.getPosition && m.getPosition().toJSON())}
        />
      )}

      {/* Extra fallback marker — always visible at known coords to confirm marker rendering */}
      <Marker
        position={fallbackCenter}
        title="Fallback test marker"
        onLoad={(m) => console.log("Fallback test Marker loaded:", m, "position:", m?.getPosition && m.getPosition().toJSON())}
      />

      {/* Circle */}
      {normalized && <Circle center={normalized} radius={radiusMeters} options={{ strokeColor: "#1976D2", fillOpacity: 0.06 }} onLoad={(c)=>console.log("Circle loaded", c)} />}

      {/* Donor markers */}
      {Array.isArray(donors) &&
        donors.map((donor) => {
          const lat = Number(donor?.location?.latitude);
          const lng = Number(donor?.location?.longitude);
          if (Number.isNaN(lat) || Number.isNaN(lng)) {
            console.log("Skipping donor with invalid coords:", donor);
            return null;
          }
          return (
            <Marker
              key={donor.id || donor.uid || donor.email || Math.random()}
              position={{ lat, lng }}
              onLoad={(m) => console.log("Donor Marker loaded:", donor, "markerObj:", m)}
            />
          );
        })}
    </GoogleMap>
  );
}
