// src/firebaseMessaging.js
import { getMessaging, getToken, onMessage, deleteToken } from "firebase/messaging";
import { doc, setDoc, deleteDoc } from "firebase/firestore";
import { auth, db } from "./components/firebase"; // your existing modular firebase.js
import { onAuthStateChanged } from "firebase/auth";

const VAPID_KEY = "BIO2Mc6NhNj7m9xAN8OctwR5lWFytEx-1cnnjpqTTWHeJBPbwg0Qo4LbbVrEmmkfjju5835EtZD03Mgmo7K1dmc"; // paste the VAPID public key

let messagingInstance = null;

export function initMessaging() {
  if (typeof window === "undefined" || !("serviceWorker" in navigator)) return;
  try {
    messagingInstance = getMessaging();
  } catch (e) {
    console.warn("FCM init failed", e);
  }
}

// Call this after user logs in (or once app starts if user already logged in)
export async function registerTokenForUser(uid) {
  if (!uid) return null;
  if (!messagingInstance) initMessaging();
  if (!messagingInstance) return null;

  try {
    // Ensure the service worker is registered
    const swReg = await navigator.serviceWorker.register("/firebase-messaging-sw.js");
    // Ask permission
    const permission = await Notification.requestPermission();
    if (permission !== "granted") {
      console.warn("Notification permission not granted");
      return null;
    }

    // Get FCM token
    const token = await getToken(messagingInstance, { vapidKey: VAPID_KEY, serviceWorkerRegistration: swReg });
    if (!token) {
      console.warn("No FCM token returned");
      return null;
    }

    // Save token to Firestore: either UserTokens/<uid> or update Donors/<uid>.fcmToken
    await setDoc(doc(db, "UserTokens", uid), { token, updatedAt: new Date().toISOString() });

    // Optional: also update Donors collection if that user is a donor record
    await setDoc(doc(db, "Donors", uid), { fcmToken: token }, { merge: true });

    console.log("FCM token saved for user", uid);
    return token;
  } catch (err) {
    console.error("registerTokenForUser error", err);
    return null;
  }
}

export async function removeTokenForUser(uid) {
  if (!uid) return;
  try {
    // Remove token document
    await deleteDoc(doc(db, "UserTokens", uid));
    // Also remove from Donors doc (optional)
    await setDoc(doc(db, "Donors", uid), { fcmToken: null }, { merge: true });

    // If you have the active token, delete from FCM SDK so it is invalidated
    if (messagingInstance) {
      try {
        // get current token - (getToken may return null). If you want to delete, call deleteToken(messagingInstance, token)
        // Skip if not available.
      } catch (e) { /* ignore */ }
    }
    console.log("FCM token removed for user", uid);
  } catch (err) {
    console.error("removeTokenForUser error", err);
  }
}

// Optional: in-app foreground callback
export function subscribeOnMessage(cb) {
  if (!messagingInstance) initMessaging();
  if (!messagingInstance) return;
  onMessage(messagingInstance, (payload) => {
    if (typeof cb === "function") cb(payload);
  });
}

// Auto-register token when user signs in; remove on sign out.
// Add this call once in your App entry (see next step)
export function setupAutoRegistration() {
  onAuthStateChanged(auth, async (user) => {
    if (user) {
      // register token for signed-in user
      await registerTokenForUser(user.uid);
    } else {
      // logged out: delete token from DB
      // If you stored uid earlier, remove it. Here we can't access previous uid; ensure you remove on logout flow.
    }
  });
}
