// SearchResult.jsx
import React, { useState } from "react";
import ContactModal from "./ContactModal";
import "./SearchResult.css";

function SearchResult({ results = [], focusOn }) {
  const [selectedUser, setSelectedUser] = useState(null);

  return (
    <section className="search-result">
      <div className="container">
        <div id="searchResults" className="search-results">
          <h3 className="results-title">Available Donors</h3>

          {(!results || results.length === 0) ? (
            <p>No donors found for the selected blood type and radius.</p>
          ) : (
            <div id="donorCards" className="donor-grid">
              {results.map((user, idx) => {
                const lat = user?.location?.latitude ?? user?.location?.lat;
                const lng = user?.location?.longitude ?? user?.location?.lng;

                return (
                  <div key={user.id || user.uid || user.email || idx} className="donor-card">
                    <div className="donor-card-header">
                      <span className="donor-name">{user.name || user.fullname}</span>
                      <span className="donor-blood">{user.bloodGroup || user.bloodType}</span>
                    </div>
                    <div className="donor-details">
                      <p>Location: {user.city || user.address || "Unknown"}</p>
                      <p>Contact: {user.contact || user.phoneNo || "Not provided"}</p>
                      <p>Email: {user.email || "—"}</p>
                      <p>
                        Status:{" "}
                        <span className={user.status === "Available" ? "status-available" : "status-unavailable"}>
                          {user.status === "Available" ? "Available" : "Not Available"}
                        </span>
                      </p>
                    </div>

                    <div className="donor-card-actions">
                      <button
                        className="contact-btn"
                        onClick={() => setSelectedUser(user)}
                      >
                        Contact Donor
                      </button>

                      <button
                        className="focus-btn"
                        onClick={() => {
                          if (typeof focusOn === "function" && lat != null && lng != null) {
                            focusOn(Number(lat), Number(lng), 15);
                          } else {
                            console.warn("focusOn is not available or donor coords invalid", lat, lng);
                          }
                        }}
                      >
                        Show on map
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {selectedUser && (
        <ContactModal user={selectedUser} onClose={() => setSelectedUser(null)} />
      )}
    </section>
  );
}

export default SearchResult;
