// src/components/ContactModal.jsx
import React from "react";
import "./ContactModal.css";

/**
 * ContactModal
 * - user: donor object (may use different key names)
 * - onClose: close callback
 *
 * This component is defensive: it accepts many possible key names and
 * renders sensible fallbacks when data is missing.
 */
export default function ContactModal({ user, onClose }) {
  if (!user) return null;

  const escapeHtml = (str) => {
    if (str === null || str === undefined) return "";
    return String(str)
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
  };

  // Name: accept fullname, name
  const name = user.fullname || user.name || user.displayName || user.full_name || "Donor";

  // Email
  const email = user.email || user.mail || "Not provided";

  // Phone: phoneNo, contact, phone
  const phone = user.phoneNo || user.contact || user.phone || "Not provided";

  // Blood group: bloodType, bloodGroup
  const bloodGroup = user.bloodType || user.bloodGroup || user.blood || "Unknown";

  // Location: prefer address / city, else use coordinates if available
  const address = user.address || user.city || user.location?.address || "";
  let locationText = address;
  if (!locationText) {
    const lat = user.location?.latitude ?? user.location?.lat;
    const lng = user.location?.longitude ?? user.location?.lng;
    if (Number.isFinite(Number(lat)) && Number.isFinite(Number(lng))) {
      locationText = `${Number(lat).toFixed(6)}, ${Number(lng).toFixed(6)}`;
    } else {
      locationText = "Not provided";
    }
  }

  // Status
  const status = user.status || user.availability || null;

  return (
    <div id="contactModal" className="modal" role="dialog" aria-modal="true">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Contact Donor</h3>
          <button
            className="modal-close"
            onClick={onClose}
            aria-label="Close contact modal"
          >
            &times;
          </button>
        </div>

        <div className="modal-body">
          <p><strong>Name:</strong> {escapeHtml(name)}</p>
          <p><strong>Blood Group:</strong> {escapeHtml(bloodGroup)}</p>
          <p><strong>Location:</strong> {escapeHtml(locationText)}</p>

          <p>
            <strong>Phone:</strong>{" "}
            {phone && phone !== "Not provided" ? (
              <a href={`tel:${escapeHtml(phone)}`}>{escapeHtml(phone)}</a>
            ) : (
              "Not provided"
            )}
          </p>

          <p>
            <strong>Email:</strong>{" "}
            {email && email !== "Not provided" ? (
              <a href={`mailto:${escapeHtml(email)}`}>{escapeHtml(email)}</a>
            ) : (
              "Not provided"
            )}
          </p>

          {status && (
            <p>
              <strong>Status:</strong>{" "}
              <span className={status === "Available" ? "status-available" : "status-unavailable"}>
                {escapeHtml(status)}
              </span>
            </p>
          )}

          <p className="contact-note">
            Please be respectful when contacting donors. Explain your situation clearly and provide your contact information.
          </p>
        </div>

        <div className="modal-footer">

          <button
            className="request-button"
            onClick={() => {
              console.log("REQUEST SENT TO DONOR:", user.email);
              // later you will replace this with Firestore + Email + Cloud Messaging
              alert(`Request sent to ${user.fullname || user.email}`);
            }}
          >
            Request Blood
          </button>


          <button className="contact-button" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
}
