import React, { useState } from 'react';
import './Header.css';
import Modal from './modal';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from "./firebase";

function Header() {

    const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
    const [isRegisterModalOpen, setIsRegisterModalOpen] = useState(false);
    
    //Login States
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    //Register States
    const [registerfullname, setRegisterfullname] = useState('');
    const [registerEmail, setRegisterEmail] = useState('');
    const [registerPassword, setRegisterPassword] = useState('');

    const handleSignIn = (e) => {
        e.preventDefault();
        console.log("Email:",email);
        console.log("Password:",password);
    };

    const handleRegister = async (e) =>{
        e.preventDefault();
        try{
            await createUserWithEmailAndPassword(auth, email, password);
            const user = auth.currentUser;
            console.log(user); 
        } catch(error){
            console.log(error); 
        }
        console.log("Name:",registerfullname);
        console.log("Email:",registerEmail);
        console.log("Password:",registerPassword);
        console.log('Register Submitted!!');
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header-contents">
                    <div className="logo">
                        <h1>ü©∏LifeLink</h1>
                    </div>
                    <nav className="nav">
                        <ul>
                            <li><a href="#home">Home</a></li>
                            <li><a href="#search">Find Donors</a></li>
                            <li><a href="#register">Register</a></li>
                            <li><a href="#about">About</a></li>
                            
                            <li><a href="tel:011-23359379" className="emergency-button">Emergency</a></li>
                        </ul>
                    </nav>
                    <div className="user-login">
                        <ul>
                            <li>
                                <a href="#" onClick={(e) => {
                                    e.preventDefault();
                                    setIsLoginModalOpen(true);
                                }}>Login</a>
                            </li>
                        </ul>
                    </div>
                    <button className="mobile-menu-toggle" aria-label="Toggle-mobile-view">
                        <span></span>
                        <span></span>
                        <span></span>
                    </button>
                </div>
            </div>
            <Modal isOpen={isLoginModalOpen} onClose={() => setIsLoginModalOpen(false)}>
                <div className="icon-wrapper">
                    <span className="icon">üîë</span>
                </div>
                <h2>Sign In</h2>
                <form onSubmit={handleSignIn} className="sign-in-form">
                    <label htmlFor="email">Email</label>
                    <input type="email" 
                            name="email" 
                            id="email" 
                            placeholder="Email" required 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                    />
                    <label htmlFor="password">Password</label>
                    <input type="password" 
                            name="password" 
                            id="password" 
                            placeholder="Password" required
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="submit-btn" type="submit">Get Started</button>
                </form>
                <div className="signin-footer">
                    <span>New User?</span>
                    <button className="register-btn" 
                        onClick=  {(e) => {
                            e.preventDefault();
                            setIsLoginModalOpen(false);
                            setIsRegisterModalOpen(true);
                        }}
                    >
                        Register
                    </button>
                </div>
            </Modal>

            <Modal isOpen={isRegisterModalOpen} onClose={() => setIsRegisterModalOpen(false)}>
                <div className="icon-wrapper">
                    <span className="icon">üìù</span>
                </div>
                <h2>Register</h2>
                <form onSubmit={handleRegister} className="sign-in-form">
                    <label htmlFor="fullname">Full Name</label>
                    <input type="text"
                           name="fullname" 
                           id="fullname" 
                           placeholder="Full Name" required
                           value={registerfullname}
                           onChange={(e) => setRegisterfullname(e.target.value)}
                    />
                    <label htmlFor="register-email">Email</label>
                    <input type="email" 
                           name="email" 
                           id="register-email" 
                           placeholder="Email" required 
                           value={registerEmail}
                           onChange={(e) => setRegisterEmail(e.target.value)}
                    />
                    <label htmlFor="register-password">Password</label>
                    <input type="password"
                           name="password" 
                           id="register-password" placeholder="Password" required
                           value={registerPassword}
                           onChange={(e) => setRegisterPassword(e.target.value)}
                    />
                    <button className="submit-btn" type="submit">Create Account</button>
                </form>
                <div className="signin-footer">
                    <span>Already have an account?</span>
                    <button 
                        className="register-btn"
                        onClick={(e) => {
                            e.preventDefault();
                            setIsRegisterModalOpen(false);
                            setIsLoginModalOpen(true);
                        }}
                    >
                        Login
                    </button>
                </div>
            </Modal>
        </header>
    )
}

export default Header;
