// src/components/Header.jsx
import React, { useState, useEffect } from 'react';
import './Header.css';
import Modal from './modal';
import { signOut } from 'firebase/auth';
import { auth } from "./firebase";
import Login from "./Login";
import SignUp from "./SignUp";   // FIXED â€” correct component name

function Header({ isLoginModalOpen, setIsLoginModalOpen }) {

    const [isSignupModalOpen, setIsSignupModalOpen] = useState(false); // FIXED
    const [user, setUser] = useState(null);

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(currentUser => {
            setUser(currentUser);
        });
        return () => unsubscribe();
    }, []);

    const handleLogout = () => {
        signOut(auth)
            .then(() => {
                console.log("User logged out");
            })
            .catch(error => {
                console.error("Logout error", error);
            });
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header-contents">
                    <div className="logo">
                        <h1>ðŸ©¸LifeLink</h1>
                    </div>

                    <nav className="nav">
                        <ul>
                            <li><a href="#home">Home</a></li>
                            <li><a href="#search">Find Donors</a></li>
                            <li><a href="#register">Register</a></li>
                            <li><a href="#about">About</a></li>
                            <li><a href="tel:011-23359379" className="emergency-button">Emergency</a></li>
                        </ul>
                    </nav>

                    <div className="user-login">
                        <ul>
                            <li>
                                {user ? (
                                    <button onClick={handleLogout} className="logout-btn">Logout</button>
                                ) : (
                                    <a
                                        href="#"
                                        onClick={e => {
                                            e.preventDefault();
                                            setIsLoginModalOpen(true); // Login modal
                                        }}
                                    >
                                        Login
                                    </a>
                                )}
                            </li>
                        </ul>
                    </div>

                    <button className="mobile-menu-toggle" aria-label="Toggle-mobile-view">
                        <span></span>
                        <span></span>
                        <span></span>
                    </button>
                </div>
            </div>

            {/* LOGIN MODAL */}
            <Modal
                isOpen={isLoginModalOpen}
                onClose={() => setIsLoginModalOpen(false)}
            >
                <Login
                    onOpenRegister={() => {
                        setIsLoginModalOpen(false);
                        setIsSignupModalOpen(true);
                    }}
                    closeLoginModal={() => setIsLoginModalOpen(false)}
                />
            </Modal>

            {/* SIGNUP MODAL â€” FIXED */}
            <Modal
                isOpen={isSignupModalOpen}
                onClose={() => setIsSignupModalOpen(false)}
            >
                <SignUp
                    onOpenLogin={() => {
                        setIsSignupModalOpen(false);
                        setIsLoginModalOpen(true);
                    }}
                    onClose={() => setIsSignupModalOpen(false)} // IMPORTANT
                />
            </Modal>
        </header>
    );
}

export default Header;
